import style from './courseList.module.scss';
import {useState} from "react";

const CourseList = (props)=> {
    let [value, setValue] = useState(0);

    const changeValue = (e) => {
        setValue(value = e.currentTarget.value)
    }

    const getValue = (e) => {
        console.log(e.currentTarget.value)
    }

    return (
      <section className={style.courseContainer}>
          <div>
              <input onChange={getValue}  type="number"/>
              <select onChange={getValue}>
                  <option value={props.courseUSD}>USD</option>
                  <option value={props.courseEUR}>EUR</option>
              </select>
          </div>
          <div>
              <input type="number"/>
              <select>
                  <option value={props.courseUSD}>USD</option>
                  <option value={props.courseEUR}>EUR</option>
              </select>
          </div>
      </section>
    )
}

export default CourseList;
