import {currencyAPI, ordersAPI} from "../api/api";

const SET_COURSE = 'SET_COURSE';

let initialState = {
    course: 0,
    limit: 4
}

const orderReducer = (state = initialState, action) => {
    switch (action.type) {
        case SET_COURSE :
            let newCourse = {course: action.course};
            let stateCopy = {...state};
            stateCopy.course = [...state.course];
            newCourse.orders.map(order => stateCopy.orders.push(order))
            return stateCopy;
        default:
            return state;
    }
}

export default orderReducer;

export const setCourse = (course) => ({type: SET_COURSE, course})

export const getCourse= () => {
    return (dispatch) => {
        currencyAPI.getCurrencyAPI().then(data => {
            dispatch(setCourse(data))
        })
    }
}
