import React from 'react';
import style from './courseList.module.scss';
import {useState} from "react";

const CourseList = (props)=> {
    let [valueFirst, setValueFirst] = useState(0);
    let [valueLast, setValueLast] = useState(0);

    let firstSelectValue = React.createRef();
    let lastSelectValue = React.createRef();

    const changeInputLast = (e) => {
        switch(firstSelectValue.current.value) {
            case 'UAH':
            console.log('hey UAH')
            break

            case 'USD':
            console.log('hey USD')
            break

            case 'EUR':
            console.log('hey EUR')
            break
        }
        // let changingValue = e.currentTarget.value * lastSelectValue.current.value;
        setValueFirst(valueFirst = e.currentTarget.value)
        // setValueLast(valueLast = changingValue)
    }

    const changeSelectLast = (e) => {
        // let changingValue = e.currentTarget.value * valueFirst;
        // setValueLast(valueLast = changingValue)
    }

    return (
      <section className={style.courseContainer}>
          <div>
              <input onChange={changeInputLast} value={valueFirst}  type="number"/>
              <select ref={firstSelectValue}>
                  <option value='USD'>USD</option>
                  <option value='EUR'>EUR</option>
                  <option value='UAH'>UAH</option>
              </select>
          </div>
          <div>
              <input type="number" value={valueLast}/>
              <select onChange={changeSelectLast} ref={lastSelectValue}>
                  <option value='USD'>USD</option>
                  <option value='EUR'>EUR</option>
                  <option value='UAH'>UAH</option>
              </select>
          </div>
      </section>
    )
}

export default CourseList;
