import React from 'react';
import style from './courseList.module.scss';
import {useState} from "react";

const CourseList = (props)=> {
    let [value, setValue] = useState(0);

    let firstInput = React.createRef();
    let lastInput = React.createRef();

    const changeValue = (e) => {
        console.log(lastInput.current.value)
        setValue(value = e.currentTarget.value)
        console.log(e.currentTarget.value)
    }

    return (
      <section className={style.courseContainer}>
          <div>
              <input onChange={changeValue} value={value}  type="number"/>
              <select ref={firstInput}>
                  <option value={props.courseUSD}>USD</option>
                  <option value={props.courseEUR}>EUR</option>
              </select>
          </div>
          <div>
              <input onChange={changeValue} type="number" value={value}/>
              <select ref={lastInput}>
                  <option value={props.courseUSD}>USD</option>
                  <option value={props.courseEUR}>EUR</option>
              </select>
          </div>
      </section>
    )
}

export default CourseList;
